(()=>{"use strict";const e=document.getElementById("city-input");document.getElementById("submit-btn").addEventListener("click",(t=>{t.preventDefault(),(async e=>{try{const t=`https://api.openweathermap.org/data/2.5/weather?q=${e}&appid=7892818979a2e87a17bcda46fe0dbc53`,n=await fetch(t,{mode:"cors"}),a=await n.json(),s=(a.weather[0].icon,document.createElement("img"));document.getElementById("info").innerHTML=`\n            <span class="display-1 text-white">${Math.round(a.main.temp_max-273)}&#176C</span><br>\n            <span class="display-6 text-white">${a.name}</span><br>  <span class=" text-white d-inline">${a.sys.country}</span>\n            `,document.getElementById("icon").appendChild(s),document.getElementById("forecast").innerHTML=`\n        <span class="display-4 text-white">${a.weather[0].description}</span> \n        `}catch(e){document.getElementById("info").textContent="City not found"}})(e.value),e.value=""}))})();