(()=>{"use strict";document.getElementById("city-input").addEventListener("input",(e=>{try{(async e=>{document.getElementById("submit-btn").addEventListener("click",(async t=>{const n=document.getElementById("icon");t.preventDefault();const a=`https://api.openweathermap.org/data/2.5/weather?q=${e}&appid=7892818979a2e87a17bcda46fe0dbc53`,c=await fetch(a),i=await c.json();console.log(i.weather[0].description),t.target&&(document.getElementById("temp").innerHTML=`<span class="display-1 text-muted">${Math.round(i.main.temp_max-273)}&#176C</span>`,"scattered clouds"===i.weather[0].description&&(n.innerHTML='<img src="./images/cloud.png" class="img-fluid img-thumbnail"/>'))}))})(e.target.value)}catch(e){console.error(e)}}))})();