(()=>{"use strict";document.getElementById("city-input").addEventListener("input",(t=>{try{(async t=>{document.getElementById("submit-btn").addEventListener("click",(async e=>{const n=`https://api.openweathermap.org/data/2.5/weather?q=${t}&appid=7892818979a2e87a17bcda46fe0dbc53`,a=await fetch(n),s=await a.json(),i=`http://openweathermap.org/img/w/${s.weather[0].icon}.png`,c=document.createElement("img");c.setAttribute("src",`${i}`),c.classList.add("icon"),e.target&&(document.getElementById("info").innerHTML=`\n            <span class="display-1 text-white">${Math.round(s.main.temp_max-273)}&#176C</span><br>\n            <span class="display-6 text-white">${s.name}</span><br>  <span class=" text-white d-inline">${s.sys.country}</span>\n            `,document.getElementById("icon").appendChild(c),document.getElementById("forecast").innerHTML=`\n        <span class="display-4 text-white">${s.weather[0].description}</span> \n        `)}))})(t.target.value)}catch(t){return t}}))})();