(()=>{"use strict";const e=document.getElementById("city-input");document.getElementById("submit-btn").addEventListener("click",(t=>{t.preventDefault(),(async e=>{try{const t=`https://api.openweathermap.org/data/2.5/weather?q=${e}&appid=7892818979a2e87a17bcda46fe0dbc53`,n=await fetch(t,{mode:"cors"}),a=await n.json(),o=`http://openweathermap.org/img/w/${a.weather[0].icon}.png`;document.querySelector("img").src=`${o}`,document.getElementById("city").innerText=`${a.name}`,document.getElementById("country").innerText=`${a.sys.country}`,document.getElementById("c").addEventListener("click",(e=>{e.preventDefault(),document.getElementById("temp").innerHTML=`<strong> ${Math.round(a.main.temp_max-273)} &#176C</strong>`})),document.getElementById("f").addEventListener("click",(e=>{e.preventDefault();const t=Math.round(a.main.temp_max-273);if(e.target){const e=9*t/5+32;document.getElementById("temp").innerHTML=`<strong> ${e} &#176F</strong>`}})),document.getElementById("temp").innerHTML=`<strong> ${Math.round(a.main.temp_max-273)} &#176C</strong>`,document.getElementById("forecast").innerText=`${a.weather[0].description}`}catch(e){document.getElementById("not-found").innerHTML='<span class="display-3 text-danger">City not found</span>'}})(e.value),e.value="",t.stopPropagation()}))})();