(()=>{"use strict";document.getElementById("city-input").addEventListener("input",(e=>{try{(async e=>{document.getElementById("submit-btn").addEventListener("click",(async t=>{const n=document.getElementById("icon");t.preventDefault();const a=`https://api.openweathermap.org/data/2.5/weather?q=${e}&appid=7892818979a2e87a17bcda46fe0dbc53`,i=await fetch(a),d=await i.json();console.log(d.weather[0].description),t.target&&(document.getElementById("info").innerHTML=`\n            <div class="display-1 text-muted">${Math.round(d.main.temp_max-273)}&#176C</div><br>\n            <div class="display-6 text-muted">${d.name}</div>\n            `,"scattered clouds"===d.weather[0].description&&(n.innerHTML='<img src="./images/cloud.png" class="img-fluid img-thumbnail"/>'))}))})(e.target.value)}catch(e){console.error(e)}}))})();