(()=>{"use strict";const e=document.getElementById("city-input");document.getElementById("submit-btn").addEventListener("click",(t=>{t.preventDefault(),(async e=>{try{const t=`https://api.openweathermap.org/data/2.5/weather?q=${e}&appid=7892818979a2e87a17bcda46fe0dbc53`,n=await fetch(t,{mode:"cors"}),o=await n.json(),a=`http://openweathermap.org/img/w/${o.weather[0].icon}.png`;document.querySelector("img").src=`${a}`,document.getElementById("city").innerText=`${o.name}`,document.getElementById("country").innerText=`${o.sys.country}`,document.getElementById("temp").innerHTML=`<strong> ${Math.round(o.main.temp_max-273)} &#176C</strong>`,document.getElementById("forecast").innerText=`${o.weather[0].description}`,body=document.querySelector("body"),body.style.backgroundColor=`${a}`}catch(e){document.getElementById("not-found").innerHTML='<span class="display-3 text-danger">City not found</span>'}})(e.value),e.value="",t.stopPropagation()}))})();