(()=>{"use strict";document.getElementById("city-input").addEventListener("input",(t=>{try{(async t=>{document.getElementById("submit-btn").addEventListener("click",(async e=>{e.preventDefault();const a=`https://api.openweathermap.org/data/2.5/weather?q=${t}&appid=7892818979a2e87a17bcda46fe0dbc53`,n=await fetch(a),c=await n.json();console.log(c.main.temp),document.getElementById("val").textContent=Math.round(c.main.temp_max-273)}))})(t.target.value)}catch(t){console.error(t)}}))})();